package utils;

public class FindERSite {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		
	
		
		String seq1 = "".toUpperCase();

//		StringBuilder  out = new StringBuilder("".toUpperCase());
		StringBuilder  out = new StringBuilder(5004);
		
		for (int i=0;i<5004;i++) {
			out.append("-");
		}
		
		String[] sites = {"GAGCTC","CTCGAG","AAGCTT","GGATCC","TCTAGA"};
		
		for (String site : sites) {
			
			int l = 0;
			int p = 0;
			do {

				p = seq1.indexOf(site,l);
				if (p>=0) out.replace(p, p+6,site);
				l=p+1;
//				System.out.println(out.toString());
			} while (p>=0);
		}
		
		System.out.println(out.toString());
		System.out.println(out.toString().length());
		
	}

}
